import React, { useState, useEffect } from 'react';
import { notificationService } from '../../services/notificationService';
import LoadingSpinner from '../../components/common/LoadingSpinner';
// Comment out or remove this line temporarily:
// import './NotificationsPage.css';

const NotificationsPage = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchNotifications();
  }, []);

  const fetchNotifications = async () => {
    try {
      const response = await notificationService.getNotifications();
      if (response.success) {
        setNotifications(response.data);
      } else {
        setError('Failed to fetch notifications');
      }
    } catch (err) {
      setError('An error occurred while fetching notifications');
    } finally {
      setLoading(false);
    }
  };

  const handleMarkAsRead = async (id) => {
    try {
      const response = await notificationService.markAsRead(id);
      if (response.success) {
        setNotifications(notifications.map(notification => 
          notification.id === id ? { ...notification, read: true } : notification
        ));
      }
    } catch (err) {
      setError('Failed to mark notification as read');
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  return (
    <div className="notifications-page" style={{ padding: '20px 0' }}>
      <h1 style={{ fontSize: '2rem', marginBottom: '30px', color: 'var(--text-color)' }}>Notifications</h1>
      
      {error && (
        <div className="alert alert-danger">
          {error}
        </div>
      )}
      
      <div className="notifications-list" style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
        {notifications.length === 0 ? (
          <div className="no-notifications" style={{ textAlign: 'center', padding: '40px', backgroundColor: '#f9f9f9', borderRadius: '10px', color: 'var(--text-muted)' }}>
            <p>No notifications found</p>
          </div>
        ) : (
          notifications.map(notification => (
            <div 
              key={notification.id} 
              className={`notification-card ${notification.read ? 'read' : 'unread'}`}
              style={{
                background: 'white',
                borderRadius: '10px',
                padding: '20px',
                boxShadow: '0 5px 15px rgba(0, 0, 0, 0.05)',
                borderLeft: notification.read ? '4px solid transparent' : '4px solid var(--primary-color)',
                backgroundColor: notification.read ? 'white' : '#f8f9ff',
                opacity: notification.read ? 0.8 : 1
              }}
            >
              <div className="notification-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
                <h3 style={{ fontSize: '1.2rem', color: 'var(--text-color)' }}>{notification.title}</h3>
                <span className="notification-date" style={{ color: 'var(--text-muted)', fontSize: '0.9rem' }}>{notification.date}</span>
              </div>
              <div className="notification-content">
                <p style={{ color: 'var(--text-color)', lineHeight: '1.5', marginBottom: '15px' }}>{notification.message}</p>
              </div>
              <div className="notification-footer" style={{ display: 'flex', justifyContent: 'flex-end' }}>
                {!notification.read && (
                  <button 
                    className="btn btn-sm btn-outline"
                    onClick={() => handleMarkAsRead(notification.id)}
                    style={{
                      padding: '5px 10px',
                      fontSize: '0.8rem',
                      backgroundColor: 'transparent',
                      border: '1px solid var(--primary-color)',
                      color: 'var(--primary-color)',
                      borderRadius: 'var(--border-radius)',
                      cursor: 'pointer'
                    }}
                  >
                    Mark as Read
                  </button>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default NotificationsPage;
