// src/pages/member/NotificationsPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const MemberNotificationsPage = () => {
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  const styles = {
    page: {
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    },
    pageHeader: {
      marginBottom: '30px'
    },
    backBtn: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      padding: '8px 16px',
      backgroundColor: '#f8f9fa',
      border: '1px solid #dee2e6',
      borderRadius: '4px',
      color: '#495057',
      fontSize: '14px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      marginBottom: '16px',
      alignSelf: 'flex-start'
    },
    headerTitle: {
      fontSize: '32px',
      color: '#2c3e50',
      margin: '0 0 8px 0'
    },
    headerSubtitle: {
      fontSize: '16px',
      color: '#7f8c8d',
      margin: '0'
    },
    loadingContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '200px'
    },
    spinner: {
      border: '4px solid rgba(0, 0, 0, 0.1)',
      borderRadius: '50%',
      borderTop: '4px solid #3498db',
      width: '40px',
      height: '40px',
      animation: 'spin 1s linear infinite'
    },
    notificationsSummary: {
      display: 'flex',
      gap: '20px',
      alignItems: 'center',
      marginBottom: '30px',
      flexWrap: 'wrap'
    },
    summaryCard: {
      background: 'white',
      borderRadius: '12px',
      padding: '20px',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)',
      minWidth: '150px'
    },
    summaryCardUnread: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white'
    },
    summaryTitle: {
      fontSize: '14px',
      fontWeight: '500',
      margin: '0 0 8px 0',
      opacity: '0.9'
    },
    summaryValue: {
      fontSize: '28px',
      fontWeight: '700',
      margin: '0'
    },
    markAllReadBtn: {
      padding: '10px 20px',
      backgroundColor: '#3498db',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      fontSize: '14px',
      cursor: 'pointer',
      transition: 'background-color 0.3s'
    },
    notificationsContainer: {
      background: 'white',
      borderRadius: '12px',
      padding: '24px',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
    },
    containerHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '20px',
      flexWrap: 'wrap',
      gap: '16px'
    },
    containerTitle: {
      fontSize: '20px',
      color: '#2c3e50',
      margin: '0'
    },
    filterControls: {
      display: 'flex',
      gap: '12px'
    },
    filterSelect: {
      padding: '8px 12px',
      border: '1px solid #ddd',
      borderRadius: '4px',
      fontSize: '14px',
      backgroundColor: 'white',
      cursor: 'pointer'
    },
    notificationsList: {
      display: 'flex',
      flexDirection: 'column',
      gap: '16px'
    },
    notificationItem: {
      display: 'flex',
      gap: '16px',
      padding: '20px',
      borderRadius: '8px',
      transition: 'all 0.3s ease',
      border: '1px solid #e9ecef'
    },
    notificationItemUnread: {
      backgroundColor: '#f8f9fa',
      borderLeft: '4px solid #3498db'
    },
    notificationItemRead: {
      backgroundColor: 'white',
      borderLeft: '4px solid transparent'
    },
    notificationIcon: {
      display: 'flex',
      alignItems: 'flex-start',
      paddingTop: '4px'
    },
    notificationContent: {
      flex: '1'
    },
    notificationHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '8px'
    },
    notificationTitle: {
      fontSize: '18px',
      fontWeight: '600',
      color: '#2c3e50',
      margin: '0'
    },
    priorityBadge: {
      display: 'inline-block',
      padding: '4px 8px',
      borderRadius: '12px',
      fontSize: '10px',
      fontWeight: '600',
      textTransform: 'uppercase'
    },
    priorityHigh: {
      backgroundColor: '#f8d7da',
      color: '#721c24'
    },
    priorityMedium: {
      backgroundColor: '#fff3cd',
      color: '#856404'
    },
    priorityLow: {
      backgroundColor: '#d4edda',
      color: '#155724'
    },
    notificationMessage: {
      fontSize: '14px',
      color: '#495057',
      margin: '0 0 12px 0',
      lineHeight: '1.5'
    },
    notificationFooter: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    notificationDate: {
      fontSize: '12px',
      color: '#6c757d'
    },
    markReadBtn: {
      background: 'none',
      border: 'none',
      color: '#3498db',
      fontSize: '12px',
      cursor: 'pointer',
      textDecoration: 'underline',
      transition: 'color 0.3s'
    }
  };

  useEffect(() => {
    const mockNotifications = [
      {
        id: 1,
        title: 'Elevator Maintenance',
        message: 'Scheduled for tomorrow 9AM-12PM. Please use stairs during this time.',
        date: '25/10/2025',
        type: 'maintenance',
        read: false,
        priority: 'high'
      },
      {
        id: 2,
        title: 'Payment Due Reminder',
        message: 'Your monthly maintenance fee of â‚¹5,000 is due by November 5.',
        date: '24/10/2025',
        type: 'payment',
        read: false,
        priority: 'medium'
      },
      {
        id: 3,
        title: 'Diwali Celebration',
        message: 'Join us for Diwali celebration on October 28 at 7PM in the community hall.',
        date: '23/10/2025',
        type: 'event',
        read: true,
        priority: 'low'
      }
    ];

    setTimeout(() => {
      setNotifications(mockNotifications);
      setLoading(false);
    }, 1000);
  }, []);

  const handleMarkAsRead = (id) => {
    setNotifications(notifications.map(notification =>
      notification.id === id ? { ...notification, read: true } : notification
    ));
  };

  const handleMarkAllAsRead = () => {
    setNotifications(notifications.map(notification => ({ ...notification, read: true })));
  };

  const getNotificationIcon = (type) => {
    switch(type) {
      case 'maintenance':
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="#ff9800">
            <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M12,4A8,8 0 0,1 20,12A8,8 0 0,1 12,20A8,8 0 0,1 4,12A8,8 0 0,1 12,4M12,6A6,6 0 0,0 6,12A6,6 0 0,0 12,18A6,6 0 0,0 18,12A6,6 0 0,0 12,6M12,8A4,4 0 0,1 16,12A4,4 0 0,1 12,16A4,4 0 0,1 8,12A4,4 0 0,1 12,8Z"/>
          </svg>
        );
      case 'payment':
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="#4caf50">
            <path d="M5,6H23V18H5V6M14,9A3,3 0 0,1 17,12A3,3 0 0,1 14,15A3,3 0 0,1 11,12A3,3 0 0,1 14,9M9,8A2,2 0 0,1 7,10V14A2,2 0 0,1 9,16H19A2,2 0 0,1 21,14V10A2,2 0 0,1 19,8H9M1,10H3V20H19V22H1V10Z"/>
          </svg>
        );
      default:
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="#607d8b">
            <path d="M13,13H11V7H13M13,17H11V15H13M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2Z"/>
          </svg>
        );
    }
  };

  const getPriorityBadge = (priority) => {
    const badges = {
      high: { class: styles.priorityHigh, text: 'HIGH' },
      medium: { class: styles.priorityMedium, text: 'MEDIUM' },
      low: { class: styles.priorityLow, text: 'LOW' }
    };
    return badges[priority] || badges.medium;
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  if (loading) {
    return (
      <div style={styles.page}>
        <div style={styles.loadingContainer}>
          <div style={styles.spinner}></div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.page}>
      <div style={styles.pageHeader}>
        <button style={styles.backBtn} onClick={() => navigate('/member')}>
          <svg width="24" height="24" viewBox="0 0 24 24" fill="#333">
            <path d="M20,11V13H8L13.5,18.5L12.08,19.92L4.16,12L12.08,4.08L13.5,5.5L8,11H20Z"/>
          </svg>
          Back to Dashboard
        </button>
        <h1 style={styles.headerTitle}>Notifications</h1>
        <p style={styles.headerSubtitle}>Stay updated with the latest community announcements</p>
      </div>

      <div style={styles.notificationsSummary}>
        <div style={styles.summaryCard}>
          <h3 style={styles.summaryTitle}>Total Notifications</h3>
          <p style={styles.summaryValue}>{notifications.length}</p>
        </div>
        <div style={{...styles.summaryCard, ...styles.summaryCardUnread}}>
          <h3 style={styles.summaryTitle}>Unread</h3>
          <p style={styles.summaryValue}>{unreadCount}</p>
        </div>
        {unreadCount > 0 && (
          <button style={styles.markAllReadBtn} onClick={handleMarkAllAsRead}>
            Mark All as Read
          </button>
        )}
      </div>

      <div style={styles.notificationsContainer}>
        <div style={styles.containerHeader}>
          <h2 style={styles.containerTitle}>All Notifications</h2>
          <div style={styles.filterControls}>
            <select style={styles.filterSelect}>
              <option>All Types</option>
              <option>Maintenance</option>
              <option>Payment</option>
              <option>Event</option>
            </select>
            <select style={styles.filterSelect}>
              <option>All Status</option>
              <option>Read</option>
              <option>Unread</option>
            </select>
          </div>
        </div>

        <div style={styles.notificationsList}>
          {notifications.map(notification => (
            <div 
              key={notification.id} 
              style={{
                ...styles.notificationItem,
                ...(notification.read ? styles.notificationItemRead : styles.notificationItemUnread)
              }}
            >
              <div style={styles.notificationIcon}>
                {getNotificationIcon(notification.type)}
              </div>
              <div style={styles.notificationContent}>
                <div style={styles.notificationHeader}>
                  <h3 style={styles.notificationTitle}>{notification.title}</h3>
                  <span style={{...styles.priorityBadge, ...getPriorityBadge(notification.priority).class}}>
                    {getPriorityBadge(notification.priority).text}
                  </span>
                </div>
                <p style={styles.notificationMessage}>{notification.message}</p>
                <div style={styles.notificationFooter}>
                  <span style={styles.notificationDate}>{notification.date}</span>
                  {!notification.read && (
                    <button 
                      style={styles.markReadBtn}
                      onClick={() => handleMarkAsRead(notification.id)}
                    >
                      Mark as Read
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MemberNotificationsPage;
