import React, { useState, useEffect } from 'react';
import { complaintService } from '../../services/complaintService';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import './ComplaintsPage.css';

const ComplaintsPage = () => {
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newComplaint, setNewComplaint] = useState({
    title: '',
    description: ''
  });

  useEffect(() => {
    fetchComplaints();
  }, []);

  const fetchComplaints = async () => {
    try {
      const response = await complaintService.getComplaints();
      if (response.success) {
        setComplaints(response.data);
      } else {
        setError('Failed to fetch complaints');
      }
    } catch (err) {
      setError('An error occurred while fetching complaints');
    } finally {
      setLoading(false);
    }
  };

  const handleAddComplaint = async (e) => {
    e.preventDefault();
    try {
      const response = await complaintService.addComplaint({
        ...newComplaint,
        date: new Date().toISOString().split('T')[0],
        status: 'pending'
      });
      if (response.success) {
        setComplaints([...complaints, response.data]);
        setNewComplaint({ title: '', description: '' });
        setShowAddForm(false);
      } else {
        setError('Failed to add complaint');
      }
    } catch (err) {
      setError('An error occurred while adding complaint');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewComplaint({
      ...newComplaint,
      [name]: value
    });
  };

  if (loading) {
    return (
      <div className="loading-container">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  return (
    <div className="complaints-page">
      <div className="complaints-header">
        <h1>My Complaints</h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowAddForm(!showAddForm)}
        >
          Add Complaint
        </button>
      </div>
      
      {error && (
        <div className="alert alert-danger">
          {error}
        </div>
      )}
      
      {showAddForm && (
        <div className="add-complaint-form">
          <h3>Add New Complaint</h3>
          <form onSubmit={handleAddComplaint}>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={newComplaint.title}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={newComplaint.description}
                onChange={handleInputChange}
                rows="3"
                required
              ></textarea>
            </div>
            <div className="form-actions">
              <button type="submit" className="btn btn-primary">Submit Complaint</button>
              <button 
                type="button" 
                className="btn btn-outline"
                onClick={() => setShowAddForm(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
      
      <div className="complaints-list">
        {complaints.length === 0 ? (
          <div className="no-complaints">
            <p>No complaints found</p>
          </div>
        ) : (
          complaints.map(complaint => (
            <div key={complaint.id} className="complaint-card">
              <div className="complaint-header">
                <h3>{complaint.title}</h3>
                <span className={`status-badge ${complaint.status}`}>
                  {complaint.status}
                </span>
              </div>
              <div className="complaint-content">
                <p>{complaint.description}</p>
              </div>
              <div className="complaint-footer">
                <span className="complaint-date">{complaint.date}</span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ComplaintsPage;
