// src/App.js
import React, { useState, useEffect, createContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Import Public Pages
import Home from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';

// Import Admin Components
import AdminDashboard from './pages/admin/AdminDashboard';
import AdminExpensesPage from './pages/admin/ExpensesPage';
import AdminComplaintsPage from './pages/admin/ComplaintsPage';
import AdminNotificationsPage from './pages/admin/NotificationsPage';

// Import Member Components
import MemberDashboard from './pages/member/MemberDashboard';
import MemberExpensesPage from './pages/member/ExpensesPage';
import MemberComplaintsPage from './pages/member/ComplaintsPage';
import MemberNotificationsPage from './pages/member/NotificationsPage';

// Import CSS
import './App.css';

// Create Auth Context
export const AuthContext = createContext();

// Protected Route Component
const ProtectedRoute = ({ children, requiredRole }) => {
  const { user } = useAuth();
  
  if (!user) {
    return <Navigate to="/login" replace />;
  }
  
  if (requiredRole && user.role !== requiredRole) {
    return <Navigate to="/unauthorized" replace />;
  }
  
  return children;
};

// Custom hook to use auth
export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Auth Provider Component
const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in on app start
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  const login = (email, password) => {
    return new Promise((resolve, reject) => {
      // Simulate API call
      setTimeout(() => {
        // Mock users database
        const users = [
          {
            id: 1,
            name: 'Administrator',
            email: 'admin@redvalley.com',
            password: 'admin123',
            role: 'admin'
          },
          {
            id: 2,
            name: 'John Doe',
            email: 'john@redvalley.com',
            password: 'member123',
            role: 'member',
            unit: 'A-101'
          },
          {
            id: 3,
            name: 'Jane Smith',
            email: 'jane@redvalley.com',
            password: 'member123',
            role: 'member',
            unit: 'B-205'
          }
        ];

        const foundUser = users.find(u => u.email === email && u.password === password);
        
        if (foundUser) {
          const userToSave = { ...foundUser };
          delete userToSave.password; // Remove password from saved user
          setUser(userToSave);
          localStorage.setItem('user', JSON.stringify(userToSave));
          resolve(userToSave);
        } else {
          reject(new Error('Invalid email or password'));
        }
      }, 1000);
    });
  };

  const register = (userData) => {
    return new Promise((resolve, reject) => {
      // Simulate API call
      setTimeout(() => {
        // Check if email already exists
        const existingUsers = JSON.parse(localStorage.getItem('registeredUsers') || '[]');
        const emailExists = existingUsers.some(u => u.email === userData.email);
        
        if (emailExists) {
          reject(new Error('Email already registered'));
          return;
        }

        // Create new user
        const newUser = {
          id: Date.now(),
          ...userData,
          role: 'member'
        };

        // Save to registered users
        existingUsers.push(newUser);
        localStorage.setItem('registeredUsers', JSON.stringify(existingUsers));

        // Auto login after registration
        const userToSave = { ...newUser };
        delete userToSave.password;
        setUser(userToSave);
        localStorage.setItem('user', JSON.stringify(userToSave));
        
        resolve(userToSave);
      }, 1000);
    });
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const value = {
    user,
    login,
    register,
    logout,
    loading
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Main App Component
const App = () => {
  return (
    <AuthProvider>
      <Router>
        <div className="app">
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<HomePage />} />
            <Route path="/home" element={<HomePage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            
            {/* Admin Protected Routes */}
            <Route 
              path="/admin/*" 
              element={
                <ProtectedRoute requiredRole="admin">
                  <AdminRoutes />
                </ProtectedRoute>
              } 
            />
            
            {/* Member Protected Routes */}
            <Route 
              path="/member/*" 
              element={
                <ProtectedRoute requiredRole="member">
                  <MemberRoutes />
                </ProtectedRoute>
              } 
            />
            
            {/* Unauthorized Page */}
            <Route 
              path="/unauthorized" 
              element={
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  height: '100vh',
                  textAlign: 'center',
                  fontFamily: 'Arial, sans-serif'
                }}>
                  <h1 style={{ fontSize: '48px', color: '#e74c3c', marginBottom: '16px' }}>403</h1>
                  <h2 style={{ fontSize: '24px', color: '#2c3e50', marginBottom: '16px' }}>Unauthorized</h2>
                  <p style={{ fontSize: '16px', color: '#7f8c8d', marginBottom: '24px' }}>
                    You don't have permission to access this page.
                  </p>
                  <button 
                    onClick={() => window.location.href = '/'}
                    style={{
                      padding: '12px 24px',
                      backgroundColor: '#3498db',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      fontSize: '14px',
                      cursor: 'pointer'
                    }}
                  >
                    Go to Home
                  </button>
                </div>
              } 
            />
            
            {/* 404 Page */}
            <Route 
              path="*" 
              element={
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  height: '100vh',
                  textAlign: 'center',
                  fontFamily: 'Arial, sans-serif'
                }}>
                  <h1 style={{ fontSize: '48px', color: '#e74c3c', marginBottom: '16px' }}>404</h1>
                  <h2 style={{ fontSize: '24px', color: '#2c3e50', marginBottom: '16px' }}>Page Not Found</h2>
                  <p style={{ fontSize: '16px', color: '#7f8c8d', marginBottom: '24px' }}>
                    The page you're looking for doesn't exist.
                  </p>
                  <button 
                    onClick={() => window.location.href = '/'}
                    style={{
                      padding: '12px 24px',
                      backgroundColor: '#3498db',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      fontSize: '14px',
                      cursor: 'pointer'
                    }}
                  >
                    Go to Home
                  </button>
                </div>
              } 
            />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
};

