// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect } from 'react';

// Import Components
import AdminDashboard from './pages/admin/AdminDashboard';
import AdminExpensesPage from './pages/admin/ExpensesPage';
import AdminComplaintsPage from './pages/admin/ComplaintsPage';
import AdminNotificationsPage from './pages/admin/NotificationsPage';

import MemberDashboard from './pages/member/MemberDashboard';
import MemberExpensesPage from './pages/member/ExpensesPage';
import MemberComplaintsPage from './pages/member/ComplaintsPage';
import MemberNotificationsPage from './pages/member/NotificationsPage';

// Import CSS
import './App.css';

// Simple Auth Context
const AuthContext = React.createContext();

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate checking authentication
    const checkAuth = () => {
      // In a real app, you would check localStorage, cookies, or make an API call
      const savedUser = localStorage.getItem('user');
      if (savedUser) {
        setUser(JSON.parse(savedUser));
      } else {
        // Default to admin for demo purposes
        const defaultUser = {
          id: 1,
          name: 'Administrator',
          role: 'admin',
          email: 'admin@redvalley.com'
        };
        setUser(defaultUser);
        localStorage.setItem('user', JSON.stringify(defaultUser));
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  const login = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const switchRole = () => {
    if (user?.role === 'admin') {
      const memberUser = {
        id: 2,
        name: 'John Doe',
        role: 'member',
        email: 'john.doe@redvalley.com',
        unit: 'A-101'
      };
      login(memberUser);
    } else {
      const adminUser = {
        id: 1,
        name: 'Administrator',
        role: 'admin',
        email: 'admin@redvalley.com'
      };
      login(adminUser);
    }
  };

  if (loading) {
    return (
      <div className="app-loading">
        <div className="spinner"></div>
        <p>Loading application...</p>
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, login, logout, switchRole }}>
      <Router>
        <div className="app">
          {/* Header with User Info and Role Switcher */}
          <header className="app-header">
            <div className="header-content">
              <div className="logo">
                <h1>Red Valley Apartment</h1>
                <span>Society Management System</span>
              </div>
              
              <div className="user-info">
                <div className="user-details">
                  <span className="user-name">{user?.name}</span>
                  <span className="user-role">{user?.role === 'admin' ? 'Administrator' : 'Resident'}</span>
                  {user?.role === 'member' && user?.unit && (
                    <span className="user-unit">Unit: {user.unit}</span>
                  )}
                </div>
                
                <div className="header-actions">
                  <button className="role-switcher" onClick={switchRole}>
                    Switch to {user?.role === 'admin' ? 'Member' : 'Admin'} View
                  </button>
                  <button className="logout-btn" onClick={logout}>
                    Logout
                  </button>
                </div>
              </div>
            </div>
          </header>

          {/* Main Content */}
          <main className="app-main">
            <Routes>
              {/* Default redirect based on user role */}
              <Route 
                path="/" 
                element={<Navigate to={`/${user?.role}`} replace />} 
              />
              
              {/* Admin Routes */}
              <Route path="/admin" element={<AdminDashboard />} />
              <Route path="/admin/dashboard" element={<AdminDashboard />} />
              <Route path="/admin/expenses" element={<AdminExpensesPage />} />
              <Route path="/admin/complaints" element={<AdminComplaintsPage />} />
              <Route path="/admin/notifications" element={<AdminNotificationsPage />} />
              
              {/* Member Routes */}
              <Route path="/member" element={<MemberDashboard />} />
              <Route path="/member/dashboard" element={<MemberDashboard />} />
              <Route path="/member/expenses" element={<MemberExpensesPage />} />
              <Route path="/member/complaints" element={<MemberComplaintsPage />} />
              <Route path="/member/notifications" element={<MemberNotificationsPage />} />
              
              {/* Catch all route */}
              <Route 
                path="*" 
                element={
                  <div className="not-found">
                    <h2>Page Not Found</h2>
                    <p>The page you're looking for doesn't exist.</p>
                    <button onClick={() => window.history.back()}>
                      Go Back
                    </button>
                  </div>
                } 
              />
            </Routes>
          </main>

          {/* Footer */}
          <footer className="app-footer">
            <div className="footer-content">
              <p>&copy; 2025 Red Valley Apartment Society Management System</p>
              <div className="footer-links">
                <a href="#" onClick={(e) => e.preventDefault()}>Privacy Policy</a>
                <a href="#" onClick={(e) => e.preventDefault()}>Terms of Service</a>
                <a href="#" onClick={(e) => e.preventDefault()}>Contact Support</a>
              </div>
            </div>
          </footer>
        </div>
      </Router>
    </AuthContext.Provider>
  );
};

export default App;
export { AuthContext };
