import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import './ComplaintsPage.css';

const ComplaintsPage = () => {
  const { currentUser } = useAuth();
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('All Status');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newComplaint, setNewComplaint] = useState({
    title: '',
    description: '',
    category: 'Maintenance',
    assignedTo: '',
    priority: 'Medium'
  });

  useEffect(() => {
    // Simulate fetching complaints
    setTimeout(() => {
      setComplaints([
        {
          id: 1,
          title: 'Water Leakage in Bathroom - Flat B-12',
          description: 'There is a water leakage in the bathroom of Flat B-12. The issue has been persisting for the past 3 days and needs immediate attention.',
          category: 'Maintenance',
          date: '2025-10-14',
          status: 'Pending',
          priority: 'High',
          reportedBy: 'John Doe',
          flat: 'B-12'
        },
        {
          id: 2,
          title: 'Elevator Not Working - Block B',
          description: 'The elevator in Block B is not functioning properly. It stops between floors sometimes, causing inconvenience to residents.',
          category: 'Maintenance',
          date: '2025-10-12',
          status: 'In Progress',
          priority: 'High',
          reportedBy: 'Jane Smith',
          flat: 'B-5'
        },
        {
          id: 3,
          title: 'Garbage Disposal Issue',
          description: 'Garbage is not being collected regularly from the designated area in Block B. Residents are facing hygiene issues.',
          category: 'Housekeeping',
          date: '2025-10-10',
          status: 'Resolved',
          priority: 'Medium',
          reportedBy: 'Mike Johnson',
          flat: 'B-8'
        },
        {
          id: 4,
          title: 'Street Light Not Working',
          description: 'The street light near the parking area is not working for the past week, causing security concerns during night time.',
          category: 'Maintenance',
          date: '2025-10-08',
          status: 'Pending',
          priority: 'Low',
          reportedBy: 'Sarah Wilson',
          flat: 'B-3'
        },
        {
          id: 5,
          title: 'Noise from Upstairs Flat',
          description: 'Excessive noise from the flat above during late night hours. Multiple residents have complained about this issue.',
          category: 'Other',
          date: '2025-10-05',
          status: 'Resolved',
          priority: 'Medium',
          reportedBy: 'Robert Brown',
          flat: 'B-7'
        },
        {
          id: 6,
          title: 'Parking Space Dispute',
          description: 'Two residents are disputing over parking space allocation in Block B. Need intervention from management.',
          category: 'Parking',
          date: '2025-10-03',
          status: 'In Progress',
          priority: 'Medium',
          reportedBy: 'Lisa Anderson',
          flat: 'B-11'
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const handleSubmitComplaint = (e) => {
    e.preventDefault();
    const newId = Date.now();
    const complaint = {
      id: newId,
      title: newComplaint.title,
      description: newComplaint.description,
      category: newComplaint.category,
      date: new Date().toISOString().split('T')[0],
      status: 'Pending',
      priority: newComplaint.priority,
      reportedBy: 'Admin',
      flat: 'Office'
    };
    
    setComplaints([complaint, ...complaints]);
    setNewComplaint({ title: '', description: '', category: 'Maintenance', assignedTo: '', priority: 'Medium' });
    setShowAddForm(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewComplaint({
      ...newComplaint,
      [name]: value
    });
  };

  const handleStatusChange = (id, newStatus) => {
    setComplaints(complaints.map(complaint => 
      complaint.id === id ? { ...complaint, status: newStatus } : complaint
    ));
  };

  const filteredComplaints = complaints.filter(complaint => {
    if (filter !== 'All Status' && complaint.status !== filter) return false;
    return true;
  });

  const pendingCount = complaints.filter(c => c.status === 'Pending').length;
  const inProgressCount = complaints.filter(c => c.status === 'In Progress').length;
  const resolvedCount = complaints.filter(c => c.status === 'Resolved').length;

  if (loading) {
    return (
      <div className="complaints-page">
        <div className="loading-container">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="complaints-page">
      <div className="complaints-header">
        <h1>Complaints Management</h1>
        <div className="complaint-stats">
          <div className="stat-card">
            <span className="stat-number">{complaints.length}</span>
            <span className="stat-label">Total</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{pendingCount}</span>
            <span className="stat-label">Pending</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{inProgressCount}</span>
            <span className="stat-label">In Progress</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{resolvedCount}</span>
            <span className="stat-label">Resolved</span>
          </div>
        </div>
      </div>
      
      <div className="complaints-controls">
        <div className="filter-controls">
          <select 
            className="filter-dropdown" 
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
          >
            <option>All Status</option>
            <option>Pending</option>
            <option>In Progress</option>
            <option>Resolved</option>
          </select>
          
          <button 
            className="add-complaint-btn"
            onClick={() => setShowAddForm(!showAddForm)}
          >
            {showAddForm ? 'Cancel' : 'Add Complaint'}
          </button>
        </div>
      </div>
      
      {showAddForm && (
        <div className="add-complaint-form">
          <h3>Add New Complaint</h3>
          <form onSubmit={handleSubmitComplaint}>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={newComplaint.title}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="category">Category</label>
                <select
                  id="category"
                  name="category"
                  value={newComplaint.category}
                  onChange={handleInputChange}
                >
                  <option value="Maintenance">Maintenance</option>
                  <option value="Housekeeping">Housekeeping</option>
                  <option value="Security">Security</option>
                  <option value="Noise">Noise</option>
                  <option value="Parking">Parking</option>
                  <option value="Other">Other</option>
                </select>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="priority">Priority</label>
                <select
                  id="priority"
                  name="priority"
                  value={newComplaint.priority}
                  onChange={handleInputChange}
                >
                  <option value="High">High</option>
                  <option value="Medium">Medium</option>
                  <option value="Low">Low</option>
                </select>
              </div>
              
              <div className="form-group">
                <label htmlFor="assignedTo">Assigned To</label>
                <input
                  type="text"
                  id="assignedTo"
                  name="assignedTo"
                  value={newComplaint.assignedTo}
                  onChange={handleInputChange}
                  placeholder="Enter staff name"
                />
              </div>
            </div>
            
            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={newComplaint.description}
                onChange={handleInputChange}
                rows="4"
                required
              ></textarea>
            </div>
            
            <div className="form-actions">
              <button type="submit" className="submit-btn">Submit Complaint</button>
              <button type="button" className="cancel-btn" onClick={() => setShowAddForm(false)}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
      
      <div className="complaints-list">
        {filteredComplaints.length === 0 ? (
          <div className="no-complaints">
            <p>No complaints found</p>
          </div>
        ) : (
          filteredComplaints.map(complaint => (
            <div 
              key={complaint.id} 
              className={`complaint-item ${complaint.status.toLowerCase().replace(' ', '-')}`}
            >
              <div className="complaint-header">
                <div className="complaint-title">
                  <h3>{complaint.title}</h3>
                  <div className="complaint-meta">
                    <span className={`status-badge ${complaint.status.toLowerCase().replace(' ', '-')}`}>
                      {complaint.status}
                    </span>
                    <span className="complaint-category">{complaint.category}</span>
                    <span className={`priority-badge ${complaint.priority.toLowerCase()}`}>
                      {complaint.priority}
                    </span>
                  </div>
                </div>
                <div className="complaint-date">{complaint.date}</div>
              </div>
              
              <div className="complaint-content">
                <p>{complaint.description}</p>
                <div className="complaint-info">
                  <span className="info-item">
                    <strong>Reported By:</strong> {complaint.reportedBy}
                  </span>
                  {complaint.flat && (
                    <span className="info-item">
                      <strong>Flat:</strong> {complaint.flat}
                    </span>
                  )}
                </div>
              </div>
              
              <div className="complaint-actions">
                {complaint.status === 'Pending' && (
                  <>
                    <button 
                      className="action-btn"
                      onClick={() => handleStatusChange(complaint.id, 'In Progress')}
                    >
                      Start Progress
                    </button>
                    <button 
                      className="action-btn"
                      onClick={() => handleStatusChange(complaint.id, 'Resolved')}
                    >
                      Mark Resolved
                    </button>
                  </>
                )}
                {complaint.status === 'In Progress' && (
                  <>
                    <button className="action-btn">Update Status</button>
                    <button 
                      className="action-btn"
                      onClick={() => handleStatusChange(complaint.id, 'Resolved')}
                    >
                      Mark Resolved
                    </button>
                  </>
                )}
                {complaint.status === 'Resolved' && (
                  <button className="action-btn">View Details</button>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ComplaintsPage;
