// src/pages/admin/ComplaintsPage.js
import React, { useState, useEffect } from 'react';
import './ComplaintsPage.css';

const AdminComplaintsPage = () => {
  const [complaints, setComplaints] = useState([]);
  const [filteredComplaints, setFilteredComplaints] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('All Status');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const mockComplaints = [
      {
        id: 'C001',
        residentName: 'John Smith',
        unit: 'A-101',
        category: 'Plumbing',
        subject: 'Leaking kitchen sink',
        description: 'The kitchen sink has been leaking for the past two days.',
        date: '25/10/2025',
        status: 'open'
      },
      {
        id: 'C002',
        residentName: 'Emily Johnson',
        unit: 'B-205',
        category: 'Electrical',
        subject: 'Power outage in bedroom',
        description: 'Frequent power outages in the master bedroom.',
        date: '24/10/2025',
        status: 'in-progress'
      },
      {
        id: 'C003',
        residentName: 'Michael Brown',
        unit: 'C-302',
        category: 'Noise',
        subject: 'Loud music from upstairs',
        description: 'Upstairs neighbors playing loud music late at night.',
        date: '23/10/2025',
        status: 'resolved'
      },
      {
        id: 'C004',
        residentName: 'Sarah Davis',
        unit: 'A-203',
        category: 'Maintenance',
        subject: 'Elevator not working',
        description: 'Building elevator has been out of service since yesterday.',
        date: '22/10/2025',
        status: 'in-progress'
      },
      {
        id: 'C005',
        residentName: 'Robert Wilson',
        unit: 'B-102',
        category: 'Security',
        subject: 'Broken gate lock',
        description: 'Main gate lock is broken and not securing properly.',
        date: '21/10/2025',
        status: 'open'
      }
    ];

    setTimeout(() => {
      setComplaints(mockComplaints);
      setFilteredComplaints(mockComplaints);
      setIsLoading(false);
    }, 1000);
  }, []);

  const statuses = ['All Status', 'open', 'in-progress', 'resolved'];

  useEffect(() => {
    let filtered = complaints;

    if (selectedStatus !== 'All Status') {
      filtered = filtered.filter(complaint => complaint.status === selectedStatus);
    }

    if (searchTerm) {
      filtered = filtered.filter(complaint =>
        complaint.residentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        complaint.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
        complaint.unit.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredComplaints(filtered);
  }, [searchTerm, selectedStatus, complaints]);

  const handleUpdateStatus = (id, newStatus) => {
    const updatedComplaints = complaints.map(complaint =>
      complaint.id === id ? { ...complaint, status: newStatus } : complaint
    );
    setComplaints(updatedComplaints);
    alert(`投诉 ${id} 状态已更新为 ${newStatus}`);
  };

  const handleViewDetails = (complaint) => {
    alert(`查看投诉详情:\n\nID: ${complaint.id}\n住户: ${complaint.residentName}\n单元: ${complaint.unit}\n类别: ${complaint.category}\n主题: ${complaint.subject}\n描述: ${complaint.description}\n日期: ${complaint.date}\n状态: ${complaint.status}`);
  };

  return (
    <div className="admin-complaints-page">
      <div className="page-header">
        <h1>Complaints Management</h1>
        <p>Red Valley Apartment - Admin View</p>
      </div>

      <div className="complaints-controls">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search complaints..."
            className="search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="filter-container">
          <select
            className="status-select"
            value={selectedStatus}
            onChange={(e) => setSelectedStatus(e.target.value)}
          >
            {statuses.map(status => (
              <option key={status} value={status}>
                {status === 'All Status' ? status : status.replace('-', ' ').toUpperCase()}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="complaints-table-container">
        {isLoading ? (
          <div className="loading-indicator">Loading complaints data...</div>
        ) : (
          <table className="complaints-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Resident</th>
                <th>Unit</th>
                <th>Category</th>
                <th>Subject</th>
                <th>Date</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredComplaints.length > 0 ? (
                filteredComplaints.map(complaint => (
                  <tr key={complaint.id}>
                    <td>{complaint.id}</td>
                    <td>{complaint.residentName}</td>
                    <td>{complaint.unit}</td>
                    <td>{complaint.category}</td>
                    <td>{complaint.subject}</td>
                    <td>{complaint.date}</td>
                    <td>
                      <span className={`status-badge ${complaint.status}`}>
                        {complaint.status.replace('-', ' ')}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          className="view-btn"
                          onClick={() => handleViewDetails(complaint)}
                        >
                          View
                        </button>
                        {complaint.status === 'open' && (
                          <button 
                            className="progress-btn"
                            onClick={() => handleUpdateStatus(complaint.id, 'in-progress')}
                          >
                            In Progress
                          </button>
                        )}
                        {complaint.status === 'in-progress' && (
                          <button 
                            className="resolve-btn"
                            onClick={() => handleUpdateStatus(complaint.id, 'resolved')}
                          >
                            Resolve
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="8" className="no-data">No complaints found</td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default AdminComplaintsPage;
