import React, { createContext, useState, useContext } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userType, setUserType] = useState(null);

  const login = (email, password, type) => {
    console.log('Login attempt:', { email, password, type });
    
    if (type === 'admin' && email === 'admin@redvalley.com' && password === 'admin123') {
      const userData = { email, name: 'Administrator' };
      setCurrentUser(userData);
      setIsAuthenticated(true);
      setUserType('admin');
      return { success: true };
    } else if (type === 'member' && email && password) {
      const userData = { email, name: 'John Doe' };
      setCurrentUser(userData);
      setIsAuthenticated(true);
      setUserType('member');
      return { success: true };
    }
    return { success: false, message: 'Invalid credentials' };
  };

  const register = (userData) => {
    setCurrentUser(userData);
    setIsAuthenticated(true);
    setUserType('member');
    return { success: true };
  };

  const logout = () => {
    setCurrentUser(null);
    setIsAuthenticated(false);
    setUserType(null);
  };

  const value = {
    currentUser,
    isAuthenticated,
    userType,
    login,
    register,
    logout
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
