// src/pages/admin/ComplaintsPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ComplaintsPage.css';

const AdminComplaintsPage = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalComplaints: 28,
    openComplaints: 8,
    inProgress: 5,
    resolved: 15,
    urgent: 3,
    thisMonth: 12
  });
  const [complaints, setComplaints] = useState([]);
  const [filteredComplaints, setFilteredComplaints] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('All Status');
  const [selectedCategory, setSelectedCategory] = useState('All Categories');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const mockComplaints = [
      {
        id: 'C001',
        residentName: 'John Smith',
        unit: 'A-101',
        category: 'Plumbing',
        subject: 'Leaking kitchen sink',
        description: 'The kitchen sink has been leaking for the past two days.',
        date: '25/10/2025',
        status: 'open',
        priority: 'high'
      },
      {
        id: 'C002',
        residentName: 'Emily Johnson',
        unit: 'B-205',
        category: 'Electrical',
        subject: 'Power outage in bedroom',
        description: 'Frequent power outages in the master bedroom.',
        date: '24/10/2025',
        status: 'in-progress',
        priority: 'medium'
      },
      {
        id: 'C003',
        residentName: 'Michael Brown',
        unit: 'C-302',
        category: 'Noise',
        subject: 'Loud music from upstairs',
        description: 'Upstairs neighbors playing loud music late at night.',
        date: '23/10/2025',
        status: 'resolved',
        priority: 'low'
      },
      {
        id: 'C004',
        residentName: 'Sarah Davis',
        unit: 'A-203',
        category: 'Maintenance',
        subject: 'Elevator not working',
        description: 'Building elevator has been out of service since yesterday.',
        date: '22/10/2025',
        status: 'in-progress',
        priority: 'high'
      },
      {
        id: 'C005',
        residentName: 'Robert Wilson',
        unit: 'B-102',
        category: 'Security',
        subject: 'Broken gate lock',
        description: 'Main gate lock is broken and not securing properly.',
        date: '21/10/2025',
        status: 'open',
        priority: 'high'
      }
    ];

    setTimeout(() => {
      setComplaints(mockComplaints);
      setFilteredComplaints(mockComplaints);
      setLoading(false);
    }, 1000);
  }, []);

  const categories = ['All Categories', ...new Set(complaints.map(complaint => complaint.category))];
  const statuses = ['All Status', 'open', 'in-progress', 'resolved'];

  useEffect(() => {
    let filtered = complaints;

    if (selectedStatus !== 'All Status') {
      filtered = filtered.filter(complaint => complaint.status === selectedStatus);
    }

    if (selectedCategory !== 'All Categories') {
      filtered = filtered.filter(complaint => complaint.category === selectedCategory);
    }

    if (searchTerm) {
      filtered = filtered.filter(complaint =>
        complaint.residentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        complaint.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
        complaint.unit.toLowerCase().includes(searchTerm.toLowerCase()) ||
        complaint.id.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredComplaints(filtered);
  }, [searchTerm, selectedStatus, selectedCategory, complaints]);

  const handleUpdateStatus = (id, newStatus) => {
    const updatedComplaints = complaints.map(complaint =>
      complaint.id === id ? { ...complaint, status: newStatus } : complaint
    );
    setComplaints(updatedComplaints);
    alert(`投诉 ${id} 状态已更新为 ${newStatus.replace('-', ' ')}`);
  };

  const handleViewDetails = (complaint) => {
    alert(`投诉详情:\n\nID: ${complaint.id}\n住户: ${complaint.residentName}\n单元: ${complaint.unit}\n类别: ${complaint.category}\n主题: ${complaint.subject}\n描述: ${complaint.description}\n日期: ${complaint.date}\n状态: ${complaint.status}\n优先级: ${complaint.priority}`);
  };

  const getPriorityBadge = (priority) => {
    const badges = {
      high: { class: 'high', text: 'HIGH' },
      medium: { class: 'medium', text: 'MEDIUM' },
      low: { class: 'low', text: 'LOW' }
    };
    return badges[priority] || badges.medium;
  };

  return (
    <div className="admin-complaints-page">
      <div className="page-header">
        <button className="back-btn" onClick={() => navigate('/admin')}>
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="#333">
            <path d="M20,11V13H8L13.5,18.5L12.08,19.92L4.16,12L12.08,4.08L13.5,5.5L8,11H20Z"/>
          </svg>
          Back to Dashboard
        </button>
        <h1>Complaints Management</h1>
        <p>Track and resolve all resident complaints efficiently</p>
      </div>

      {loading ? (
        <div className="loading-container">
          <div className="spinner"></div>
        </div>
      ) : (
        <>
          <div className="stats-grid">
            <div className="stat-card primary">
              <div className="stat-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="#fff">
                  <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z"/>
                </svg>
              </div>
              <div className="stat-content">
                <h3>Total Complaints</h3>
                <p className="stat-value">{stats.totalComplaints}</p>
              </div>
            </div>

            <div className="stat-card warning">
              <div className="stat-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="#fff">
                  <path d="M13,13H11V7H13M13,17H11V15H13M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2Z"/>
                </svg>
              </div>
              <div className="stat-content">
                <h3>Open</h3>
                <p className="stat-value">{stats.openComplaints}</p>
              </div>
            </div>

            <div className="stat-card info">
              <div className="stat-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="#fff">
                  <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M12,4A8,8 0 0,1 20,12A8,8 0 0,1 12,20A8,8 0 0,1 4,12A8,8 0 0,1 12,4M12,6A6,6 0 0,0 6,12A6,6 0 0,0 12,18A6,6 0 0,0 18,12A6,6 0 0,0 12,6M12,8A4,4 0 0,1 16,12A4,4 0 0,1 12,16A4,4 0 0,1 8,12A4,4 0 0,1 12,8Z"/>
                </svg>
              </div>
              <div className="stat-content">
                <h3>In Progress</h3>
                <p className="stat-value">{stats.inProgress}</p>
              </div>
            </div>

            <div className="stat-card success">
              <div className="stat-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="#fff">
                  <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M12,4A8,8 0 0,1 20,12A8,8 0 0,1 12,20A8,8 0 0,1 4,12A8,8 0 0,1 12,4M11,16.5L6.5,12L7.91,10.59L11,13.67L16.59,8.09L18,9.5L11,16.5Z"/>
                </svg>
              </div>
              <div className="stat-content">
                <h3>Resolved</h3>
                <p className="stat-value">{stats.resolved}</p>
              </div>
            </div>
          </div>

          <div className="complaints-container">
            <div className="container-header">
              <h2>All Complaints</h2>
              <div className="filter-controls">
                <input
                  type="text"
                  placeholder="Search complaints..."
                  className="search-input"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <select
                  className="filter-select"
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                >
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
                <select
                  className="filter-select"
                  value={selectedStatus}
                  onChange={(e) => setSelectedStatus(e.target.value)}
                >
                  {statuses.map(status => (
                    <option key={status} value={status}>
                      {status === 'All Status' ? status : status.replace('-', ' ').toUpperCase()}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="complaints-table-container">
              <table className="complaints-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Resident</th>
                    <th>Unit</th>
                    <th>Category</th>
                    <th>Subject</th>
                    <th>Priority</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredComplaints.length > 0 ? (
                    filteredComplaints.map(complaint => (
                      <tr key={complaint.id}>
                        <td>{complaint.id}</td>
                        <td>{complaint.residentName}</td>
                        <td>{complaint.unit}</td>
                        <td>{complaint.category}</td>
                        <td>{complaint.subject}</td>
                        <td>
                          <span className={`priority-badge ${getPriorityBadge(complaint.priority).class}`}>
                            {getPriorityBadge(complaint.priority).text}
                          </span>
                        </td>
                        <td>{complaint.date}</td>
                        <td>
                          <span className={`status-badge ${complaint.status}`}>
                            {complaint.status.replace('-', ' ')}
                          </span>
                        </td>
                        <td>
                          <div className="action-buttons">
                            <button 
                              className="view-btn"
                              onClick={() => handleViewDetails(complaint)}
                            >
                              View
                            </button>
                            {complaint.status === 'open' && (
                              <button 
                                className="progress-btn"
                                onClick={() => handleUpdateStatus(complaint.id, 'in-progress')}
                              >
                                In Progress
                              </button>
                            )}
                            {complaint.status === 'in-progress' && (
                              <button 
                                className="resolve-btn"
                                onClick={() => handleUpdateStatus(complaint.id, 'resolved')}
                              >
                                Resolve
                              </button>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="9" className="no-data">No complaints found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default AdminComplaintsPage;
