import React, { useState, useEffect } from 'react';
import { expenseService } from '../../services/expenseService';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import './ExpensesPage.css';

const ExpensesPage = () => {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const response = await expenseService.getExpenses();
      if (response.success) {
        setExpenses(response.data);
      } else {
        setError('Failed to fetch expenses');
      }
    } catch (err) {
      setError('An error occurred while fetching expenses');
    } finally {
      setLoading(false);
    }
  };

  const handleMakePayment = (expenseId) => {
    // In a real app, this would navigate to a payment page
    alert(`Payment functionality would be implemented here for expense ${expenseId}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  return (
    <div className="expenses-page">
      <h1>Payment & Expenses</h1>
      
      {error && (
        <div className="alert alert-danger">
          {error}
        </div>
      )}
      
      <div className="payment-section">
        <div className="payment-card">
          <h3>Monthly Maintenance</h3>
          <div className="payment-amount">â‚¹5,000</div>
          <button 
            className="btn btn-primary"
            onClick={() => handleMakePayment('maintenance')}
          >
            Make Payment
          </button>
        </div>
      </div>
      
      <div className="expenses-list">
        <h2>Payment History</h2>
        {expenses.length === 0 ? (
          <div className="no-expenses">
            <p>No payment history found</p>
          </div>
        ) : (
          expenses.map(expense => (
            <div key={expense.id} className="expense-card">
              <div className="expense-header">
                <h3>{expense.title}</h3>
                <span className="expense-amount">{expense.amount}</span>
              </div>
              <div className="expense-footer">
                <span className="expense-date">{expense.date}</span>
                <span className="payment-status">Paid</span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ExpensesPage;
