import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import './NotificationsPage.css';

const NotificationsPage = () => {
  const { currentUser } = useAuth();
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('All Types');
  const [showUnreadOnly, setShowUnreadOnly] = useState(false);

  useEffect(() => {
    // Simulate fetching notifications
    setTimeout(() => {
      setNotifications([
        {
          id: 1,
          title: 'Payment Due',
          message: 'Your monthly maintenance payment of ₹5,000 is due by October 31, 2025',
          date: '2025-10-14',
          type: 'Payment',
          priority: 'high',
          read: false
        },
        {
          id: 2,
          title: 'Community Meeting',
          message: 'Annual general body meeting scheduled for October 25, 2025 at 6:00 PM in the community hall',
          date: '2025-10-13',
          type: 'Meeting',
          priority: 'high',
          read: false
        },
        {
          id: 3,
          title: 'Water Supply Interruption',
          message: 'Water supply will be interrupted on October 20, 2025 from 10:00 AM to 2:00 PM for maintenance work',
          date: '2025-10-12',
          type: 'Maintenance',
          priority: 'medium',
          read: true
        },
        {
          id: 4,
          title: 'Festive Celebration',
          message: 'Diwali celebration event on October 24, 2025. All residents are invited to attend',
          date: '2025-10-11',
          type: 'Event',
          priority: 'low',
          read: true
        },
        {
          id: 5,
          title: 'Security Update',
          message: 'New security guards have been appointed for night duty. Please cooperate with them',
          date: '2025-10-10',
          type: 'Security',
          priority: 'medium',
          read: true
        },
        {
          id: 6,
          title: 'Parking Allocation',
          message: 'Parking spaces for Block B have been reallocated. Please check the new allocation list',
          date: '2025-10-09',
          type: 'Parking',
          priority: 'low',
          read: true
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const handleMarkAsRead = (id) => {
    setNotifications(notifications.map(notification => 
      notification.id === id ? { ...notification, read: true } : notification
    ));
  };

  const handleMarkAllAsRead = () => {
    setNotifications(notifications.map(notification => ({ ...notification, read: true })));
  };

  const filteredNotifications = notifications.filter(notification => {
    if (showUnreadOnly && notification.read) return false;
    if (filter !== 'All Types' && notification.type !== filter) return false;
    return true;
  });

  const unreadCount = notifications.filter(n => !n.read).length;
  const highPriorityCount = notifications.filter(n => n.priority === 'high').length;

  if (loading) {
    return (
      <div className="notifications-page">
        <div className="loading-container">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="notifications-page">
      <div className="notifications-header">
        <h1>Notifications</h1>
        <div className="notification-stats">
          <div className="stat-card">
            <span className="stat-number">{notifications.length}</span>
            <span className="stat-label">Total</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{unreadCount}</span>
            <span className="stat-label">Unread</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{highPriorityCount}</span>
            <span className="stat-label">High Priority</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{notifications.length - unreadCount}</span>
            <span className="stat-label">Read</span>
          </div>
        </div>
      </div>
      
      <div className="notifications-controls">
        <div className="filter-controls">
          <select 
            className="filter-dropdown" 
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
          >
            <option>All Types</option>
            <option>Payment</option>
            <option>Meeting</option>
            <option>Maintenance</option>
            <option>Event</option>
            <option>Security</option>
            <option>Parking</option>
          </select>
          
          <button 
            className={`filter-btn ${showUnreadOnly ? 'active' : ''}`}
            onClick={() => setShowUnreadOnly(!showUnreadOnly)}
          >
            Unread Only
          </button>
          
          <button 
            className="mark-all-btn"
            onClick={handleMarkAllAsRead}
            disabled={unreadCount === 0}
          >
            Mark All Read
          </button>
        </div>
      </div>
      
      <div className="notifications-list">
        {filteredNotifications.length === 0 ? (
          <div className="no-notifications">
            <p>No notifications found</p>
          </div>
        ) : (
          filteredNotifications.map(notification => (
            <div 
              key={notification.id} 
              className={`notification-item ${notification.read ? 'read' : 'unread'}`}
            >
              <div className="notification-header">
                <div className="notification-title">
                  <h3>{notification.title}</h3>
                  <div className="notification-meta">
                    <span className={`priority-badge ${notification.priority}`}>
                      {notification.priority}
                    </span>
                    <span className="notification-type">{notification.type}</span>
                  </div>
                </div>
                <div className="notification-date">{notification.date}</div>
              </div>
              
              <div className="notification-content">
                <p>{notification.message}</p>
              </div>
              
              <div className="notification-actions">
                {!notification.read && (
                  <button 
                    className="mark-read-btn"
                    onClick={() => handleMarkAsRead(notification.id)}
                  >
                    Mark as Read
                  </button>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default NotificationsPage;
