// src/pages/admin/ExpensesPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // 添加这行导入
import './ExpensesPage.css';

const AdminExpensesPage = () => {
  const navigate = useNavigate();  // 添加这行获取navigate函数
  const [expenses, setExpenses] = useState([]);
  const [filteredExpenses, setFilteredExpenses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All Categories');
  const [isLoading, setIsLoading] = useState(true);

  // 模拟从API获取数据
  useEffect(() => {
    const mockExpenses = [
      {
        id: 1,
        date: '1/10/2025',
        category: 'Maintenance',
        description: 'Elevator repair and servicing',
        vendor: 'ABC Elevators',
        amount: 25000,
        status: 'approved'
      },
      {
        id: 2,
        date: '8/10/2025',
        category: 'Cleaning',
        description: 'Cleaning supplies and equipment',
        vendor: 'CleanPro Supplies',
        amount: 8000,
        status: 'pending'
      },
      {
        id: 3,
        date: '15/10/2025',
        category: 'Utilities',
        description: 'Monthly electricity bill',
        vendor: 'City Power Company',
        amount: 15000,
        status: 'approved'
      },
      {
        id: 4,
        date: '20/10/2025',
        category: 'Security',
        description: 'Security guard monthly salary',
        vendor: 'SecureGuard Services',
        amount: 12000,
        status: 'approved'
      },
      {
        id: 5,
        date: '25/10/2025',
        category: 'Landscaping',
        description: 'Garden maintenance and plants',
        vendor: 'GreenThumb Landscaping',
        amount: 7000,
        status: 'pending'
      },
      {
        id: 6,
        date: '28/10/2025',
        category: 'Plumbing',
        description: 'Repair of main water pipeline',
        vendor: 'QuickFix Plumbing',
        amount: 18000,
        status: 'approved'
      }
    ];

    setTimeout(() => {
      setExpenses(mockExpenses);
      setFilteredExpenses(mockExpenses);
      setIsLoading(false);
    }, 1000);
  }, []);

  // 获取所有类别
  const categories = ['All Categories', ...new Set(expenses.map(expense => expense.category))];

  // 处理搜索和筛选
  useEffect(() => {
    let filtered = expenses;

    if (selectedCategory !== 'All Categories') {
      filtered = filtered.filter(expense => expense.category === selectedCategory);
    }

    if (searchTerm) {
      filtered = filtered.filter(expense =>
        expense.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        expense.vendor.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredExpenses(filtered);
  }, [searchTerm, selectedCategory, expenses]);

  // 导出功能
  const handleExport = () => {
    alert('导出功能已触发！在实际应用中，这将下载CSV或Excel文件。');
  };

  // 更新费用状态
  const handleUpdateStatus = (id, newStatus) => {
    const updatedExpenses = expenses.map(expense =>
      expense.id === id ? { ...expense, status: newStatus } : expense
    );
    setExpenses(updatedExpenses);
    alert(`费用 #${id} 状态已更新为 ${newStatus}`);
  };

  // 格式化金额
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  return (
    <div className="admin-expenses-page">
      <div className="page-header">
        <button className="back-btn" onClick={() => navigate('/admin')}>
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="#333">
            <path d="M20,11V13H8L13.5,18.5L12.08,19.92L4.16,12L12.08,4.08L13.5,5.5L8,11H20Z"/>
          </svg>
          Back to Dashboard
        </button>
        <h1>Expenses Management</h1>
        <p>Red Valley Apartment - Admin View</p>
      </div>

      <div className="expenses-controls">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search expenses..."
            className="search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="filter-container">
          <select
            className="category-select"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        <button className="export-button" onClick={handleExport}>
          Export
        </button>
      </div>

      <div className="expenses-table-container">
        {isLoading ? (
          <div className="loading-indicator">Loading expenses data...</div>
        ) : (
          <table className="expenses-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Category</th>
                <th>Description</th>
                <th>Vendor</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredExpenses.length > 0 ? (
                filteredExpenses.map(expense => (
                  <tr key={expense.id}>
                    <td>{expense.date}</td>
                    <td>{expense.category}</td>
                    <td>{expense.description}</td>
                    <td>{expense.vendor}</td>
                    <td>{formatAmount(expense.amount)}</td>
                    <td>
                      <span className={`status-badge ${expense.status}`}>
                        {expense.status}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        {expense.status === 'pending' && (
                          <button 
                            className="approve-btn"
                            onClick={() => handleUpdateStatus(expense.id, 'approved')}
                          >
                            Approve
                          </button>
                        )}
                        <button className="view-btn">View</button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="7" className="no-data">No expenses found</td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default AdminExpensesPage;
